var selectedMaterialList = [];
var parentProductGoodses = [];
var parentProductGoodsesIds = [];
var firstStatus = true;
export default {
  name: "tp-material-dialog",
  //接收最好 是ids
  data: function data() {
    return {
      filtersData: {
        type: 2,
        //原料类型（1：工艺，2：原料）
        pageIndex: 1,
        pageSize: 10,
        searchWords: "",
        cateIdentifier: "",
        valid: true
      },
      materialDialogVisible: false,
      tableListData: [],
      total: 0,
      classData: [],
      classList: [],
      cateArr: [],
      props: {
        value: "id",
        label: "name",
        children: "children"
      },
      logType: ""
    };
  },
  created: function created() {
    firstStatus = true;
    this.init();
  },
  mounted: function mounted() {
    this.getClassData();
  },
  methods: {
    init: function init() {
      this.filtersData = {
        type: 2,
        //原料类型（1：工艺，2：原料）
        pageIndex: 1,
        pageSize: 10,
        searchWords: "",
        cateIdentifier: "",
        valid: true
      };
      this.tableListData = [];
      this.logType = "";
      this.cateArr = [];
      selectedMaterialList = [];
      parentProductGoodses = [];
      parentProductGoodsesIds = [];
    },
    getClassData: function getClassData() {
      var _this = this;

      //分类类型（1：标准分类，2：定制分类，3：原料分类或者称为属性分类）
      this.$common.getClassSelect(this, 3).then(function (res) {
        _this.classData = res.ztree;
        _this.classList = res.list;
      });
    },
    cascaderGet: function cascaderGet(e) {
      var cateId = JSON.parse(JSON.stringify(e)).pop();
      var obj = this.classList.find(function (item) {
        return item.id == cateId;
      });
      this.filtersData.materialCateIdentifier = obj.identifier;
    },
    popupInit: function popupInit(identifier, dialogType, parentList, productGoodses) {
      selectedMaterialList = [];
      parentProductGoodses = [];
      parentProductGoodsesIds = [];
      this.logType = dialogType;
      selectedMaterialList = parentList;

      if (this.logType == "edit") {
        parentProductGoodses = productGoodses;
      }

      this.selectedArr(); //第一次进入走接口

      if (firstStatus) {
        firstStatus = false;
        this.filtersData.cateIdentifier = identifier;
        this.getTableList();
      }

      this.materialDialogVisible = true;
    },
    //获取列表
    getTableList: function getTableList() {
      var _this2 = this;

      this.request(this.api.product.manufacturerProductionMaterial_list, this.filtersData).then(function (data) {
        var _data = data.data,
            _dataLen = _data.length; //全部勾选false 把ID值为空

        for (var i = 0; i < _dataLen; i++) {
          _data[i]._checkbox = false;
          _data[i].usageAmount = 1;
          _data[i].id = null;
        } //获取数据后 从已有列表中找出已勾选分类
        //循环 改变勾选状态


        var listData = selectedMaterialList; //找到已有的 改成true

        if (listData.length >= 0) {
          for (var k = 0; k < listData.length; k++) {
            for (var j = 0; j < _dataLen; j++) {
              if (listData[k].materialId == _data[j].materialId) {
                _data[j]._checkbox = true;
              }
            }
          }
        } //列表数据


        _this2.tableListData = _data;
        _this2.total = data.totalCount;
      });
    },
    //改变状态
    selectedArr: function selectedArr() {
      if (this.tableListData.length <= 0) {
        return;
      }

      for (var i = 0; i < this.tableListData.length; i++) {
        this.tableListData[i]._checkbox = false;
      }

      var listData = selectedMaterialList;

      for (var k = 0; k < listData.length; k++) {
        for (var j = 0; j < this.tableListData.length; j++) {
          if (listData[k].materialId == this.tableListData[j].materialId) {
            this.tableListData[j]._checkbox = true;
          }
        }
      }
    },

    /**
     * 单选发生改变
     * 当单选发生改变时 把新增的勾选 放入数组中
     *
     */
    tableChangeCheckbox: function tableChangeCheckbox(elem, row) {
      var _this3 = this;

      var index = selectedMaterialList.findIndex(function (item) {
        return item.materialId == row.materialId;
      });

      if (elem) {
        //新增
        if (index == -1) {
          selectedMaterialList.push(row);
        }
      } else {
        //删除
        if (this.logType == "edit") {
          var names = [];
          var productGoodses = parentProductGoodses;

          for (var i = 0; i < productGoodses.length; i++) {
            var materials = productGoodses[i].productGoodsMaterials;

            for (var k = 0; k < materials.length; k++) {
              if (materials[k].materialId == row.materialId) {
                names.push(productGoodses[i].name);
                break;
              }
            }
          }

          if (names.length > 0) {
            var strName = names.join("/");
            this.$confirm("sku".concat(strName, "\u5DF2\u4F7F\u7528\u5230\u8BE5\u9879\uFF0C\u662F\u5426\u786E\u8BA4\u5220\u9664!"), "提示", {
              confirmButtonText: "确定",
              cancelButtonText: "取消",
              type: "warning"
            }).then(function () {
              parentProductGoodsesIds.push(row.materialId);

              _this3.removeList(index);
            }).catch(function () {
              row._checkbox = true;
            });
          } else {
            this.removeList(index);
          }
        } else {
          this.removeList(index);
        }
      }
    },
    removeList: function removeList(_index) {
      if (_index != -1) {
        selectedMaterialList.splice(_index, 1);
      }
    },
    //搜索
    handleSearch: function handleSearch() {
      this.filtersData.pageIndex = 1;
      this.getTableList();
    },
    //清空重置
    handleResetFilters: function handleResetFilters(formName) {
      this.$refs[formName].resetFields();
      this.cateArr = [];
    },
    //当前页数修改时发生改变
    handleCurrentChange: function handleCurrentChange(val) {
      this.filtersData.pageIndex = val;
      this.getTableList();
    },
    // 每页显示多少条时发生改变
    handleSizeChange: function handleSizeChange(val) {
      this.filtersData.pageSize = val;
      this.getTableList();
    },

    /**
     * 修改保存
     * 数据新增 或者删除
     * 向父组件传入数据
     */
    submitForm: function submitForm() {
      this.$emit("setParentMaterialData", _toConsumableArray(selectedMaterialList), parentProductGoodsesIds);
      this.closeDialog();
    },
    //关闭
    closeDialog: function closeDialog() {
      this.materialDialogVisible = false;
    }
  }
};
