        this.addFormData.generalLedger = "";
        return;
      }

      var reg = /[^\d.]/g; // 只能是数字和小数点，不能是其他输入

      val = val.replace(reg, ""); // 保证第一位只能是数字，不能是点

      val = val.replace(/^\./g, ""); // 小数只能出现1位

      val = val.replace(".", "$#$").replace(/\./g, "").replace("$#$", "."); // 小数点后面保留3位

      val = val.replace(/^(\-)*(\d+)\.(\d\d\d).*$/, '$1$2.$3');
      this.addFormData.generalLedger = val;
