import { getToken } from "@/utils/auth";
var token = getToken(); /// localStorage.getItem('token') // 要保证取到

export default {
  props: {
    //根据type 取决于回调方法
    imgType: {
      type: String,
      default: "MASTER_IMAGE"
    },
    upIsShow: {
      type: Boolean,
      default: true
    }
  },
  data: function data() {
    return {
      dialogImgVisible: false,
      dialogImageUrl: "",
      isMultiple: false,
      isLoading: false,
      uploadData: {
        catalog: "product"
      }
    };
  },
  methods: {
    init: function init() {},
    imgAction: function imgAction() {
      return this.api.FN_ALLPATH(this.api.content.picture_upload);
    },
    handlePictureCardPreview: function handlePictureCardPreview(file) {
      //预览图片时调用
      this.dialogImageUrl = file.url;
      this.dialogImgVisible = true;
    },
    beforeAvatarUpload: function beforeAvatarUpload(file) {
      //文件上传之前调用做一些拦截限制
      var isLtM = file.size / 1024 / 1024 < 5;
      var str = file.type.split("/")[1].toLowerCase();

      if (!isLtM) {
        this.$message.error("上传图片大小不能超过" + 5 + "MB!");
        return false;
      }

      if (!str.match(/png|jpg|gif|webp|jpeg/)) {
        this.$message.error("上传图片只能是 jpeg | jpg | png格式!");
        return false;
      }

      this.isLoading = true;
      return true;
    },
    //图片上传成功
    handleAvatarSuccess: function handleAvatarSuccess(res, file) {
      this.isLoading = false; //主图

      if (this.imgType == 'MASTER_IMAGE') {
        this.$emit('setParentMasterImg', res.data);
      } else if (this.imgType == 'DETAIL_IMAGE') {
        //详情
        this.$emit('setParentDetailImg', res.data);
      } else {
        //其他
        this.$emit('setParentImg', res.data);
      } // this.$emit('parentRef',res.data.imageUrl);
      // this.imageUrl = URL.createObjectURL(file.raw);

    },
    handleExceed: function handleExceed(files, fileList) {
      //图片上传超过数量限制
      this.$message.error("只能上传一张!");
    }
  },
  watch: {
    upIsShow: function upIsShow(val) {
      this.upIsShow = val;
    }
  },
  components: {}
};
