export default {
  data: function data() {
    return {
      filtersData: {
        cateId: "",
        manufacturerId: '',
        cateName: "",
        cateIdentifier: "",
        fullCateName: '',
        manufacturerName: ''
      },
      props: {
        value: "id",
        label: "name",
        children: "children"
      },
      manufacturerOptions: [],
      //制造商
      cateArr: [],
      rules: {
        //规则验证
        cateId: [{
          required: true,
          message: "请选择分类",
          trigger: "change"
        }],
        manufacturerId: [{
          required: true,
          message: "请选择制造商",
          trigger: "change"
        }]
      },
      classData: [],
      classList: []
    };
  },
  computed: {},
  created: function created() {
    this.getClassData();
    this.getManufacturerOption();
  },
  methods: {
    manufacturerGet: function manufacturerGet(val) {
      var _obj = this.manufacturerOptions.find(function (item) {
        return item.id == val;
      });

      this.filtersData.manufacturerName = _obj.name;
    },
    //获取设计商
    getManufacturerOption: function getManufacturerOption() {
      var _this = this;

      this.request(this.api.product.manufacturer_list, {
        valid: true
      }).then(function (data) {
        // console.log(data.data);
        var _data = data.data;
        _this.manufacturerOptions = _data;
      });
    },
    getClassData: function getClassData() {
      var _this2 = this;

      this.$common.getClassSelect(this, 1).then(function (res) {
        _this2.classData = res.ztree;
        _this2.classList = res.list;
      });
    },
    cascaderGet: function cascaderGet(e) {
      var cateId = JSON.parse(JSON.stringify(e)).pop();
      var obj = this.classList.find(function (item) {
        return item.id == cateId;
      });

      if (obj.level > 0) {
        this.filtersData.cateId = cateId;
      } else {
        this.filtersData.cateId = "";
        this.$message.error("请选择二级分类");
      }

      this.filtersData.cateName = obj.name;
      this.filtersData.cateIdentifier = obj.identifier;
      this.filtersData.fullCateName = obj.fullCateName;
    },
    //获取
    handleResetFilters: function handleResetFilters(formName) {
      this.$refs[formName].resetFields();
      this.cateArr = [];
    },
    //获取数据跳转
    submitForm: function submitForm(formName) {
      var _this3 = this;

      this.$refs[formName].validate(function (valid) {
        if (valid) {
          var obj = _this3.filtersData;

          _this3.$router.push({
            path: "/product/custom_attrs/add_support_details",
            name: "add_support_details",
            query: {
              cateId: obj.cateId,
              cateName: obj.cateName,
              cateIdentifier: obj.cateIdentifier,
              fullCateName: obj.fullCateName,
              manufacturerName: obj.manufacturerName,
              manufacturerId: obj.manufacturerId,
              _type: "add"
            }
          });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    }
  }
};
