export default {
  data: function data() {
    return {
      filtersData: {
        cateId: "",
        name: ""
      },
      props: {
        value: "id",
        label: "name",
        children: "children"
      },
      isToPuth: false,
      cateArr: [],
      rules: {
        //规则验证
        cateId: [{
          required: true,
          message: "请选择分类",
          trigger: "change"
        }],
        name: [{
          required: true,
          message: "请输入原始款号",
          trigger: "blue"
        }]
      },
      classData: [],
      classList: []
    };
  },
  computed: {},
  created: function created() {
    this.getClassData();
  },
  methods: {
    getClassData: function getClassData() {
      var _this = this;

      this.$common.getClassSelect(this, 1).then(function (res) {
        _this.classData = res.ztree;
        _this.classList = res.list;
      });
    },
    cascaderGet: function cascaderGet(e) {
      var cateId = JSON.parse(JSON.stringify(e)).pop();
      var obj = this.classList.find(function (item) {
        return item.id == cateId;
      });

      if (obj.level > 0) {
        this.filtersData.cateId = cateId;
      } else {
        this.filtersData.cateId = '';
        this.$message.error('请选择二级分类');
      }

      this.filtersData.cateCode = obj.code;
      this.filtersData.cateName = obj.name;
      this.filtersData.cateIdentifier = obj.identifier;
      this.filtersData.fullCateName = obj.fullCateName;
    },
    //
    originalCode: function originalCode() {
      var _this2 = this;

      var _name = this.filtersData.name;

      if (_name == "" || _name == undefined || _name == null || _name.length > 0 && _name.trim().length == 0) {
        this.$message({
          type: "error",
          message: '原始款号不能为空！'
        });
        return false;
      }

      this.request(this.api.product.product_verificationRepeat, {
        originalCode: this.filtersData.name
      }).then(function (res) {
        _this2.isToPuth = true;

        _this2.$message({
          type: "success",
          message: '该原始款号可以使用'
        });
      });
    },
    //获取
    handleResetFilters: function handleResetFilters(formName) {
      this.$refs[formName].resetFields();
      this.cateArr = [];
    },
    //获取数据跳转
    submitForm: function submitForm(formName) {
      var _this3 = this;

      this.$refs[formName].validate(function (valid) {
        if (valid) {
          // if(!this.isToPuth){
          //    this.$message({
          //       type: "error",
          //       message: '验证不通过！'
          //     });
          //   return false
          // }
          var params = _this3.filtersData;

          _this3.$router.push({
            path: "/product/commodity/commodityDetails",
            query: {
              cateId: params.cateId,
              cateName: params.cateName,
              cateIdentifier: params.cateIdentifier,
              fullCateName: params.fullCateName,
              cateCode: params.cateCode,
              _type: "add"
            }
          });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    }
  }
};
