import { isvalidPhone } from '@/utils/validate';
import division from "@/components/division";
import LogisticsSelect from "@/views/collocation/components/LogisticsSelect";
import OrganizationSelect from "@/views/collocation/components/OrganizationSelect";

var validPhone = function validPhone(rule, value, callback) {
  if (!value) {
    callback(new Error('请输入手机号码'));
  } else if (!isvalidPhone(value) && value) {
    callback(new Error('请输入正确的11位手机号码'));
  } else {
    callback();
  }
};

export default {
  name: '',
  // 数据
  data: function data() {
    return {
      // 参数
      addFormData: {
        // 发起人
        createName: '',
        // 货主
        goodsOwner: '',
        // 机构
        organizationId: '',
        organizationName: '',
        // 订单类型
        orderType: '0204',
        // 物流
        logisticsCode: '',
        logisticsCompany: '',
        // 仓库
        warehouseCode: '',
        warehouseName: '',
        // 门店
        storeId: '',
        storeName: '',
        storeType: '',
        // 收货人
        receiveMan: '',
        // 电话
        phone: '',
        // 省市区信息
        provinceName: '',
        provinceCode: '',
        cityName: '',
        cityCode: '',
        areaName: '',
        areaCode: '',
        divisionId: '',
        // 地址
        addressInfo: ''
      },
      // 仓库列表
      warehouseList: [],
      // 门店列表
      storeList: [],
      // 验证
      rules: {
        organizationId: [{
          required: true,
          message: '请选择机构',
          trigger: 'change'
        }],
        warehouseCode: [{
          required: true,
          message: '请选择仓库',
          trigger: 'change'
        }],
        storeId: [{
          required: true,
          message: '请选择门店',
          trigger: 'change'
        }],
        receiveMan: [{
          required: true,
          message: '请输入收货人',
          trigger: 'change'
        }, {
          max: 16,
          message: '长度最多 16 个字符',
          trigger: 'change'
        }],
        phone: [{
          required: true,
          trigger: "change",
          validator: validPhone
        }],
        divisionId: [{
          required: true,
          message: '所在地区必须选到区县',
          trigger: 'change'
        }],
        addressInfo: [{
          required: true,
          message: '请输入收货地址',
          trigger: "change"
        }]
      }
    };
  },
  // 组件
  components: {
    division: division,
    LogisticsSelect: LogisticsSelect,
    OrganizationSelect: OrganizationSelect
  },
  created: function created() {},
  // 初始化数据
  mounted: function mounted() {
    var _this = this;

    this.$nextTick(function () {
      // 判断是否有门店缓存
      var storeInfo = sessionStorage.getItem('BACK_GOODS_INFO');

      if (storeInfo) {
        _this.addFormData = JSON.parse(storeInfo);

        _this.getWarehouseList(_this.addFormData.organizationId);

        _this.getStoreList(_this.addFormData.organizationId);
      }
    });
  },
  // 方法
  methods: {
    // 获取省市信息
    setDivision: function setDivision(division) {
      this.addFormData.provinceName = division.province;
      this.addFormData.provinceCode = division.provinceCode;
      this.addFormData.cityName = division.municipality;
      this.addFormData.cityCode = division.municipalityCode;
      this.addFormData.areaName = division.area;
      this.addFormData.areaCode = division.areaCode;
      this.addFormData.divisionId = division.divisionId;
    },
    // 机构选择后

    /**
     *
     * @param val
     * @param flag 标识是否是初始化 如果初始化不清空数据（ 机构组件内当机构为一个时，组件会默认选中触发回调）
     */
    organizationChange: function organizationChange(val) {
      var flag = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : false;

      if (val) {
        this.getWarehouseList(val);
        this.getStoreList(val);
        this.getGoodsOwner(val);
      }

      if (flag) return; // 仓库列表

      this.warehouseList = []; // 门店列表

      this.storeList = [];
      this.addFormData.warehouseCode = '';
      this.addFormData.storeId = '';
      this.addFormData.receiveMan = '';
      this.addFormData.phone = '';
      this.addFormData.provinceName = '';
      this.addFormData.provinceCode = '';
      this.addFormData.cityName = '';
      this.addFormData.cityCode = '';
      this.addFormData.areaName = '';
      this.addFormData.areaCode = '';
      this.addFormData.divisionId = '';
      this.addFormData.addressInfo = '';
      this.$refs.division.init();
    },
    // 取消
    cancel: function cancel() {
      var module = '/' + this.$route.path.split('/')[1];
      this.$router.push({
        path: module + '/back_goods/back_goods_list'
      });
    },
    // 确认返货
    confirmSub: function confirmSub() {
      var _this2 = this;

      if (!this.addFormData.goodsOwner) {
        this.$notify.error({
          title: '错误',
          message: '该机构下没有货主无法配货'
        });
        return;
      }

      this.$refs.addFormData.validate(function (valid) {
        if (valid) {
          // 获取下拉name
          _this2.addFormData.organizationName = _this2.$refs.organization.$refs.organization.selectedLabel;
          _this2.addFormData.warehouseName = _this2.$refs.warehouse.selectedLabel;
          _this2.addFormData.storeName = _this2.$refs.store.selectedLabel;
          _this2.addFormData.logisticsCompany = _this2.$refs.logistics.$refs.logistics.selectedLabel; // 缓存信息

          sessionStorage.setItem('BACK_GOODS_INFO', JSON.stringify(_this2.addFormData));

          var module = '/' + _this2.$route.path.split('/')[1];

          _this2.$router.push({
            path: module + '/back_goods/back_goods_add_goods'
          });
        } else {
          return false;
        }
      });
    },
    // 仓库选择后
    warehouseChange: function warehouseChange(val) {
      if (!val) {
        return;
      }

      var warehouse = this.warehouseList.find(function (item) {
        return item.code === val;
      });
      var division = JSON.parse(localStorage.getItem("division"));
      var region = division.find(function (t) {
        return t.id === warehouse.divisionId;
      });
      this.addFormData.receiveMan = warehouse.leader;
      this.addFormData.phone = warehouse.mobilenum;
      this.addFormData.provinceName = region.province;
      this.addFormData.provinceCode = region.provinceCode;
      this.addFormData.cityName = region.municipality;
      this.addFormData.cityCode = region.municipalityCode;
      this.addFormData.areaName = region.area;
      this.addFormData.areaCode = region.areaCode;
      this.addFormData.divisionId = warehouse.divisionId;
      this.addFormData.addressInfo = warehouse.address;
    },
    // 获取货主
    getGoodsOwner: function getGoodsOwner(id) {
      var _this3 = this;

      this.request(this.api.system.goodsOwner_getByOrgId, {
        orgId: id,
        used: true
      }).then(function (data) {
        if (data.code) {
          _this3.addFormData.goodsOwner = data.code;
        }
      });
    },
    //获取仓库数据
    getWarehouseList: function getWarehouseList(id) {
      var _this4 = this;

      this.request(this.api.system.warehouse_list, {
        orgId: id,
        used: true
      }).then(function (data) {
        _this4.warehouseList = data.data;
      });
    },
    //获取门店数据
    getStoreList: function getStoreList(id) {
      var _this5 = this;

      this.request(this.api.retailStore.storeBasic_listSmart, {
        organizationId: id
      }).then(function (data) {
        _this5.storeList = data;
      });
    }
  },
  // 计算属性
  computed: {},
  // 监听数据变化
  watch: {}
};
