import ImagePopover from "@/views/collocation/components/ImagePopover";
import { numberPrice } from "@/utils/validate";
export default {
  name: '',
  props: {
    // 提交门店信息
    transferSlipAddInfo: {
      type: Object,
      default(){
        return {}
      }
    },
    // 是否显示
    checkedGoodsShow: {
      type: Boolean,
      default: false
    },
    // 表格数据
    tableData: {
      type: Array
    }
  },
  // 数据
  data() {
    return {
      id:this.$route.query.id,
      value: '',
      // 分页显示的数据
      showTableData: [],
      formData: {
        pageIndex: 1,
        pageSize: 10
      },
      // 选择的商品总信息
      checkedAllSKUInfo: {
        retailPriceTotal: 0,
        typeTotal: 0,
        goodsTotal: 0,
        actualPriceTotal: 0,
        discount: ''
      },
    }
  },
  // 组件
  components: {
    ImagePopover
  },
  // 初始化数据
  mounted() {
    this.$nextTick(() => {
      this.init();
    })
  },
  // 方法
  methods: {
    init(){
      this.formData.pageIndex = 1;
      // 获取显示的数据
      this.getShowTableData();
      // 计算选中的商品总信息
      this.getCheckedAllSKUInfo();
      this.tableData.forEach((item, index) => {
        item.availableQuantity = item.availableQuantity != null ? item.availableQuantity : item.skuStockQuantity;
        this.$set(item, 'modifyPriceCopy', item.modifyPrice);
        this.$set(item, 'modifyPriceType', 0);
      });
    },
    // 获取显示数据
    getShowTableData(){
      if(this.tableData.length <= 10){
        this.showTableData = this.tableData;
      }
      if(this.tableData.length > 10){
        this.showTableData = this.tableData.slice((this.formData.pageIndex - 1) * this.formData.pageSize, this.formData.pageIndex * this.formData.pageSize )
      }
    },
    // 数量输入改变
    /**
     *
     * @param $event dom节点
     * @param scope el-table 行数据的所有信息
     */
    handleInput($event, scope){
      this.$nextTick(()=>{
        let val = $event.target.value;
        scope.row.quantity = $event.target.value = val.replace(/[^\d.]/g,'');
        this.handleChange(scope)
      })
    },
    /**
     * 数量点击改变
     * @param scope el-table 行数据的所有信息
     */
    handleChange(scope){
      if(scope.row.quantity == 0){
        this.$confirm('返货量为0时，将删除该商品', '提示', {
          confirmButtonText: '确认',
          cancelButtonText: '取消',
          showClose:false
        }).then(() => {
          this.tableData.splice(scope.$index + (this.formData.pageIndex - 1) * this.formData.pageSize, 1);
          if(this.showTableData.length === 1 && this.formData.pageIndex > 1){
            this.formData.pageIndex = this.formData.pageIndex -1;
          }
          this.getShowTableData();
          this.$nextTick(()=>{
            // 计算选中的商品总信息
            this.getCheckedAllSKUInfo();
          })
        }).catch(() => {
          scope.row.quantity = 1;
        });
      }else {
        this.$nextTick(()=>{
          // 计算选中的商品总信息
          this.getCheckedAllSKUInfo();
        });
      }
    },
    /**
     * 单价改变
     * @param $event dom节点
     * @param row 修改单价的行数据
     */
    purchasePriceChange($event, row){
      let val = $event.target.value;
      if(!val) {
        row.allotPrice = 0;
        return
      }
      numberPrice($event);
      row.allotPrice = $event.target.value;
      this.$nextTick(()=>{
        // 计算选中的商品总信息
        this.getCheckedAllSKUInfo();
      })
    },
    // 计算选中的商品总信息
    getCheckedAllSKUInfo(){
      this.checkedAllSKUInfo = {
        retailPriceTotal: 0,
        typeTotal: 0,
        goodsTotal: 0,
        actualPriceTotal: 0,
        discount: this.checkedAllSKUInfo.discount,
        skuNum:null,
        purchasePriceTotal:null
      };
      // 获取所有的 商品货号 + 颜色  集合
      let typeTotalObj = {};
      if(this.tableData.length){
        this.tableData.forEach(item => {  
          this.checkedAllSKUInfo.skuNum += 1;
          this.checkedAllSKUInfo.purchasePriceTotal += Number(item.modifyPrice) * Number(item.quantity);
          this.checkedAllSKUInfo.retailPriceTotal += Number(item.retailPrice) * Number(item.quantity);
          this.checkedAllSKUInfo.goodsTotal += Number(item.quantity);
          this.checkedAllSKUInfo.actualPriceTotal += Number(item.allotPrice) * Number(item.quantity);
          let goodsCodeColor = item.goodsCode + item.color;
          if(!typeTotalObj[goodsCodeColor]){
            typeTotalObj[goodsCodeColor] = 1
          }
        });
        this.checkedAllSKUInfo.purchasePriceTotal = this.checkedAllSKUInfo.purchasePriceTotal.toFixed(2);
        this.checkedAllSKUInfo.retailPriceTotal = this.checkedAllSKUInfo.retailPriceTotal.toFixed(2);
        this.checkedAllSKUInfo.actualPriceTotal = this.checkedAllSKUInfo.actualPriceTotal.toFixed(2);
        this.checkedAllSKUInfo.typeTotal = Object.keys(typeTotalObj).length;
      }
    },
    /**
     * 输入全部商品的折扣过滤
     * @param $event dom节点
     */
    goodsAllDiscount($event){
      let value = $event.target.value;
      this.checkedAllSKUInfo.discount = $event.target.value = value.match(/\d(\.\d?)?/g) && value.match(/\d(\.\d?)?/g)[0] || null;
    },
    // 折扣回车
    goodsAllDiscountEnter(){
      if(!this.checkedAllSKUInfo.discount) return;
      this.checkedAllSKUInfo.discount = this.checkedAllSKUInfo.discount.replace(/\.$/, '');
      if(this.tableData.length){
        this.tableData.forEach(item => {
          item.allotPrice = (item.retailPrice * Number(this.checkedAllSKUInfo.discount) / 10).toFixed(2)
        });
        // 计算选中的商品总信息
        this.getCheckedAllSKUInfo();
      }
    },
    // 继续选货
    continueCheckGoods(){
      this.$emit('update:checkedGoodsShow', false)
    },
    // 取消
    href() {
      this.$router.push({
        path: '/scb/order/sell_order'
      });
    },
    /**
     * 最终提交
     */
    saveDraft(type){
      if(!this.tableData.length){
        return
      }
      let Info = this.transferSlipAddInfo;
      let data = {
        "purchaseAgentId": Info.inStoreId,
        "purchaseAgentName": Info.inStoreName,
        "orderId": this.$route.query.id,
        "orderNum": this.$route.query.orderNum,
        "purchaseOrderStatus":Info.purchaseOrderStatus,
        divisionId:Info.divisionId,
        "receiveMan": Info.receiveMan,
        "phone": Info.phone,
        "provinceCode": Info.provinceCode,
        "provinceName": Info.provinceName,
        "cityCode": Info.cityCode,
        "cityName": Info.cityName,
        "areaCode": Info.areaCode,
        "areaName": Info.areaName,
        "addressInfo": Info.addressInfo,
        "carriage": "到付",
        purchaseOrderProductVOS: []
      };

      this.tableData.forEach(item => {
        let obj = {
          id:item.id,
          "brandId": item.productBrandId || item.brandId,
          "brandName": item.productBrandName || item.brandName,
          "color": item.color,
          "colorSeries": item.colorSeries,
          "goodsCode": item.code || item.goodsCode,
          "goodsName": item.name || item.goodsName,
          "image": item.image,
          "price": item.retailPrice || item.price,
          "productCode": item.productCode,
          // "productName": item.productName,
          purchasePrice:item.purchasePrice,
          "modifyPrice": item.modifyPrice,
          "size": item.size,
          "skuModifyQuantity": item.quantity || item.skuQuantity, 
          skuQuantity: item.quantity || item.skuQuantity,
          "skuRefundQuantity": 0
        };
        if(item.modifyPriceCopy !== item.modifyPrice) {
          obj.modifyPriceType = 1;
        }else{
          obj.modifyPriceType = 0;
        };
        // if(type == 0 && !this.$route.query.orderNum) {
        //   obj.purchasePrice = item.purchasePrice;
        // }
        // if(type == 1) {
        //   obj.purchasePrice = item.purchasePrice;
        // }
        data.purchaseOrderProductVOS.push(obj);
      });

      let url = null;
      switch(type) {
        case 0:
          url = this.api.scb.purchaseOrder_saveDraft;
          break;
        case 1:
          url = this.$route.query.orderNum 
          ? this.api.scb.purchaseOrder_modification 
          : this.api.scb.purchaseOrder_allocateCargo;
          break;
      };
      this.request(url, data).then(data => {
        sessionStorage.removeItem("TRANSFER_SLIP_APP_INFO");
        if(data && data.length){
          let html = '';
          data.forEach(errItem => {
            for(let i = this.tableData.length - 1; i >= 0; i--){
              if(this.tableData[i].code == errItem.code){
                if(errItem.stockQty == 0){  
                  this.tableData.splice(i, 1);
                  html += '<p>'+ errItem.code + '：可用库存为0，已删除该商品；</p>';
                }else {
                  html += '<p>'+ errItem.code + '：可用库存不足，已更新为最大值' + errItem.stockQty + '；</p>';
                  this.tableData[i].stockQty = errItem.stockQty;
                  this.tableData[i].quantity = errItem.stockQty;
                }
                break;
              }
            }
          });
          this.$notify({
            title: '提示',
            duration: 0,
            dangerouslyUseHTMLString: true,
            message: html
          });
          this.$nextTick(() => {
            this.init();
          })
        }else {
          this.$notify.success({ title: '成功',message: type == 1 ? '新增成功！' : '修改成功！'});
          this.$router.push({
            path: '/scb/order/sell_order'
          })
        }
      });
    },
    /**
     * 表格更改条数
     * @param val 每页的条数
     */
    handleSizeChange(val){
      this.formData.pageSize = val;
      this.formData.pageIndex = 1;
      this.getShowTableData();
    },
    /**
     * 表格更改页数
     * @param val 页码
     */
    handleCurrentChange(val){
      this.formData.pageIndex=val;
      this.getShowTableData();
    }
  },
  // 计算属性
  computed: {},
  // 监听数据变化
  watch: {

  }
}
