import axios from 'axios'
import {getToken} from '@/utils/auth'
import codeSet from '@/components/codeSet'
import treeDept from '@/components/treeDept'
import { filter } from 'minimatch';
import { mapGetters } from 'vuex';
import { get } from 'http';
import * as userTypes from "@/types/userConfig";

let token=getToken()

export default {
   name:'workOrder_beingProcessed_list',
   data() {
      return{
         ls:{},
         usersGetList:[],
         _jobOrderTime_Obj:{},
         job_list_obj:{},
         // 预警提醒列表
         "alarmSets":[
            {
               "startTime":null,
               "endTime":null,
               "warnType":null,
               "localRemind":null,
               "emailRemind":null,
               "smsRemind":null,
               "signerReceive":null,
               "adminReceive":null
            }
         ],
         priority:{
            "01":"高",
            "02":"中",
            "03":"低",
         },
         formInline:{
            user: '',
            region: ''
         },
         filtersData:{
            pageIndex: 1, //当前页码
            pageSize: 10, //每页数量
            "orderBy":"createTime",
            used: null, //有效状态
            fileteSingle: "", //搜索关键字
            name:"",
            isStart:"",
            startTime: "",
            endTime: "",
            businessType:"",//
            receiveDept:"",// 接受部门
         },
         value: '',
         storageValArr:[],
         // 指派单选框
         radio: '1',
         // 指派
         gridData: [],
        dialogTableVisible: false,
        dialogFormVisible: false,
        form: {
          name: '',
          region: '',
          date1: '',
          date2: '',
          delivery: false,
          type: [],
          resource: '',
          desc: ''
        },
        formLabelWidth: '120px'
      }
   },
   created() {
      const that = this;
      this.getWaitWorkOrder_list();

   },
   activated() {
      this.getWaitWorkOrder_list();
   },
   methods: {
      /**
       *
       */
      fnDialogTableVisible() {
         this.waitWorkOrderTransfer(this.ls);
         this.dialogTableVisible = false;
      },
       /**
       * 获取工单池指派人员列表
       */
      usersGet(obj) {
         this._jobOrderTime_Obj = Object.assign(obj, {
            updateName: this.userInfo.name
         });
         this.ls = {};
         console.log(obj);
         this.request(
            this.api.job_order.usersGet,
            {
               deptId:obj.receiveDept
            }
         ).then(res => {
            this.usersGetList = res;
            this.dialogTableVisible = true;
         });
      },
      /**
       * 处理中指派
       */
      waitWorkOrderTransfer(obj) {
         const that = this;

         Object.assign(this._jobOrderTime_Obj, {
            assignee:obj.name,
            assigneeId:obj.userId
         });
         this.request(
            this.api.job_order.waitWorkOrderTransfer,
            this._jobOrderTime_Obj
         ).then(res => {
            this.$message({
               type: 'success',
               message: '转派成功！'
            });
            // 如果数据大于一条，则直接请求列表
            if(that.job_list_obj.data.length > 1) {
               that.getWaitWorkOrder_list();
            }else{
               // 如果是第一页直接请求列表，负责页数减一再请求
               if(that.filtersData.pageIndex == 1) {
                  that.getWaitWorkOrder_list();
               }else{
                  that.filtersData.pageIndex --;
                  that.getWaitWorkOrder_list();
               }
            }
         });
      },
      /**
       * 处理中-撤销指派
       */
      waitWorkOrderRevokeTransfer(obj) {
         const that = this;

         Object.assign(obj, {
            assignee:this.userInfo.name,
            assigneeId:this.userInfo.userId
         });
         this.$confirm('确定要撤销吗？', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
         }).then(() => {
            this.request(
               this.api.job_order.waitWorkOrderRevokeTransfer,
               obj
            ).then(res => {
               this.$message({
                  type: 'success',
                  message: '撤销成功！'
               });
               // 如果数据大于一条，则直接请求列表
               if(that.job_list_obj.data.length > 1) {
                  that.getWaitWorkOrder_list();
               }else{
                  // 如果是第一页直接请求列表，负责页数减一再请求
                  if(that.filtersData.pageIndex == 1) {
                     that.getWaitWorkOrder_list();
                  }else{
                     that.filtersData.pageIndex --;
                     that.getWaitWorkOrder_list();
                  }
               }
            });
         }).catch(() => {
          this.$message({
            type: 'info',
            message: '已取消撤销！'
          });
         });

      },
      /**
       * 查看
       */
      dispose(obj, isRead) {
         this.href('workorder/category/workOrder_detail_dispose',{
            isRead,
            number:obj.number,
            sourceChileType:obj.sourceChileType,
            id:obj.id,
            sourceId:obj.sourceId,
            procInstId:obj.procInstId,
            orderSn:obj.orderSn,
            isAddCourse:true,
         })
      },
      // 点击页数
      handleCurrentChange(pageIndex){
         this.filtersData.pageIndex = pageIndex;
         this.getWaitWorkOrder_list();
      },
      // 点击每页多少条数
      handleSizeChange(pageSize){
         this.filtersData.pageSize = pageSize;
         this.getWaitWorkOrder_list();
      },
      /**
       * 查看预警
       */
      examineWarning(scope) {
         if(!scope) return;
         this.alarmSets = JSON.parse(JSON.stringify(scope));
         if(!this.alarmSets.length > 0) return;
         this.alarmSets.forEach((item, index) => {
            item.endTime = this.formattingTime(item.endTime);
         });
         this.dialogTableVisible = true;
      },
      /**
       * 查询工单列表
       */
      getWaitWorkOrder_list(pageIndex, pageSize) {
         const that = this;

         this.request(
         that.api.job_order.getWaitWorkOrder_list,
         this.filtersData
         ).then(res => {
            that.job_list_obj = res;
         });
      },
      /**
       * 格式化工单时间段
       * @param { String } str 需要转换的字符串 如：'1-0-1-0'
       * @return { String } str 如：'01-00-01-00'
       */
      formattingTime(str) {
         let arr = str.split('-');
         return `${this.replenishZero(arr[0])}天${this.replenishZero(arr[1])}时${this.replenishZero(arr[2])}分${this.replenishZero(arr[3])}秒`;
      },
      /**
       * 补零函数
       * @param { Nunber | String } item 数字或数字字符串 如： 1，'1'
       * @return { String } 如：'01'
       */
      replenishZero(item) {
         item = parseInt(item);

         return item < 10 ? `0${item}` : item;
      },
      /**
       * 跳转工单详情
       */
      href(routeName, param) {
         this.$router.push({
            path: '/' + routeName,
            query: param
         })
      },
      /**
       * 删除工单
       */
      open(scope) {
         const that = this;
         console.log(scope)
        this.$confirm('确定要删除吗？', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(() => {
          that.request(
             that.api.job_order.jobOrderTimeDelete,
             {
                id:scope.id
             }
          ).then(res => {
             this.$message({
               type: 'succeed',
               message: '删除成功！'
            });
            that.getJobOrderTime_List();
          })
        }).catch(() => {
          this.$message({
            type: 'info',
            message: '已取消删除！'
          });
        });
      },
      /**
       * 领取工单
       */
      open2() {
         this.$message({
            message: '工单领取成功！',
            type: 'success'
         });
      },
      /**
       * 模糊搜索
       */
      handleSearch() {
         this.filtersData.pageIndex = 1;
         this.getWaitWorkOrder_list();
      },
      /**
       * 搜索重置
       */
      handleResetFilters() {
         Object.assign(this.filtersData, {
            pageIndex: 1, //当前页码
            pageSize: 10, //每页数量
            "orderBy":"createTime",
            used: null, //有效状态
            fileteSingle: "", //搜索关键字
            name:"",
            isStart:"",
            startTime: "",
            endTime: "",
            businessType:"",//
            receiveDept:"",// 接受部门
         });
      },
   },
   computed: {
      ...mapGetters({
         userInfo: userTypes.GETTER_USERINFO
      })
   },
   components:{
      codeSet,
      treeDept
   },
}
