import { mapGetters } from "vuex";
import * as userTypes from "@/types/userConfig";
export default {
  data() {
    return {
      signData: {
        //交易编码来源
        sourceType:'03001',
        //搜索关键字
        searchKey:"",
        //交易类型
        tradeType:"",
        //渠道商户
        mchIds:[],
        //标记隶属机构
        organizationCodes:[],
        //机构Id的集合
        organizationIds: [],
        //收/付款方式
        paymentCodes:[],
        //交易开始时间
        tradeTimeStart:'',
        //交易结束时间
        tradeTimeEnd:'',
        //当前页数
        pageIndex: 1,
        //每页要显示的数据条数,默认10条
        pageSize: 10
      },
      //得到权限Code
      codeData: [],
      //存储表格选择项改变时的数据
      multipleSelectionData: [],
      //表格数据
      tableData: [],
      //累计交易金额
      transactionLedger:null,
      //总条数
      totalCount: 1,
      //存储门店数据
      storeListData: [],
      //隶属机构数据
      organizationListData:[],
      //交易类型数据
      tradeTypeData:[],
      //收/付款方式数据
      paymentCodesData:[],
      //对账方式
      typeListData:[
        {
          name:'日结',
          id:'1'
        },
        {
          name:'周结',
          id:'2'
        },
        {
          name:'月结',
          id:'3'
        },
        {
          name:'自定义',
          id:'4'
        }
      ]
    };
  },
  computed: {
    ...mapGetters({
        userInfo: userTypes.GETTER_USERINFO
    })
  },
  //过滤器
  filters: {
    //过滤是否对账
    filterValid(val) {
      switch (val) {
        case false:
          return "否";
          break;
        case true:
          return "是";
          break;
        default:
          return "--";
          break; 
      }
    },
    //过滤交易类型
    filterTradeType(val) {
      switch (val) {
        case "02001":
          return "销售";
          break;
        case "02002":
          return "退款";
          break;
        case "02003":
          return "配货";
          break;
        case "02004":
          return "返货";
          break;
      }
    },
    //过滤支付方式
    filerPaymentCode(val) {
      switch (val) {
         case "08001":
          return "刷卡支付";
          break;
        case "08002":
          return "微信支付";
          break;
        case "08003":
          return "支付宝支付";
          break;
        case "08004":
          return "储值卡支付";
          break;
        case "08005":
          return "现金支付";
          break;
        case "08006":
          return "支票支付";
          break;
        case "08007":
          return "电子汇款";
          break;
        case "08008":
          return "汇票";
          break;
        case "08009":
          return "其他";
          break;
        default:
          return '--'
          break;
      }
    }
  },
  mounted() {
    //得到组织机构数据
    this.getWarehouseListData();
    //得到交易类型数据
    this.getTradeTypeListData();
    //得到收付款方式数据
    this.getPaymentCodesListData();
  },
  methods: {
    //得到组织机构数据
    async getWarehouseListData() {
      let info = await this.request(this.api.user.get_userinfo, {}, true);
      console.log(info);
      //得到当前登录人的直属机构id
      this.request(this.api.user.userCompany_getUserOrgByUserId, {
        userId: info.userId,
        type: 0
      }).then(data => {
        console.log(data);
        //得到门店列表数据
        this.getStoreBasicListData(data.id);
      });
      //得到关联机构（type-0 机构 type-1 部门）
      this.request(this.api.user.userCompany_getUserAllDepartByUserId, {
        userId: info.userId,
        type: 0
      }).then(data => {
        //得到组织机构数据
        this.organizationListData=data;
        console.log(data);
        this.signData.organizationIds=[]
        data.forEach(item => {
          this.signData.organizationIds.push(item.id);
        });
        //得到列表数据
        this.getBillWaitListData();
      });
    },
    // 得到code码，用来限制是否显示按钮
    getCodeLimitToBtnStatus(codeId) {
      this.request(this.api.user.crm_userCompany_authList, {
        menuId: codeId
      }).then(res => {
        console.log(res);
        this.codeData = res;
      });
    },
    //得到门店列表数据
    getStoreBasicListData(organizationId) {
      this.request(this.api.financial.financialBase_merchantListByType, {organizationId:organizationId,merchantType:"06003"}).then(res => {
      console.log(res);
      res.forEach(item=>{
        if(item.storeName){
          item.name=item.storeName
        }
      })
      this.storeListData = res;
    });
    },
    
    //得到交易类型数据
    getTradeTypeListData(){
      this.request(this.api.financial.financialBase_transactionTypeList,{}).then(res => {
       console.log(res);
      //  this.tradeTypeData=res;
       this.tradeTypeData.push(res[0])
       this.tradeTypeData.push(res[1])
      })
    },
    //得到收付款方式数据
    getPaymentCodesListData(){
      this.request(this.api.financial.financialBase_paymentTypeList,{}).then(res => {
       console.log(res);
       this.paymentCodesData=res;
      })
    },
    //得到列表数据
    getBillWaitListData() {
      let filter = this.$common.deepCopy(this.signData);
      if (filter.tradeTimeEnd != null) {
        let date = new Date(filter.tradeTimeEnd);
        let t = (date.getTime() / 1000 + 86399) * 1000;
        filter.tradeTimeEnd = new Date(t);
      }
      this.request(
        this.api.financial.financialData_tdTransactionDetail_list,
        filter
      ).then(data => {
        this.totalCount = data.TransactionDetailList.totalCount;
        this.tableData = data.TransactionDetailList.data;
        this.transactionLedger=data.TransactionLedger
        console.log(data);
      });
      //得到按钮操作权限数据
      this.getCodeLimitToBtnStatus(this.$route.meta.currentid);
    },
    //查询表单数据
    searchFormData(ref) {
      this.signData.pageIndex=1
      if(this.signData.organizationCodes.length==0){
        this.getWarehouseListData()
      }else{
        this.signData.organizationIds=this.signData.organizationCodes
        this.getBillWaitListData();
      } 
    },
    //重置表单数据
    resetFormData(formData) {
      this.$refs[formData].resetFields();
    },
    //导出
    exportExcel() {
      console.log("导出Excel表格...+++++++++++++++++++");
      require.ensure([], () => {
        let { export_json_to_excel } = require("@/vendor/Export2Excel");
        let tHeader = [
          "交易单号",
          "是否对账",
          "交易类型",
          "交易金额",
          "收/付款方式",
          "收/付款流水号",
          "渠道商户",
          "隶属机构",
          "交易操作人",
          "交易时间",
          "对账人"
        ];
        // 上面设置Excel的表格第一行的标题
        let filterVal = [
          "orderSn",
          "validStatusName",
          "tradeTypeStatusName",
          "amount",
          "paymentCodeStatusName",
          "serialNumber",
          "merchant",
          "organization",
          "operator",
          "tradeTimeStatusName",
          "checker"
        ];
        // 上面的index、nickName、name是tableData里对象的属性
        let list = this.multipleSelectionData; //把data里的multipleSelectionData存到list
        let data = this.formatJson(filterVal, list);
        export_json_to_excel(tHeader, data, "渠道交易明细表单");
      });
    },
    formatJson(filterVal, jsonData) {
      return jsonData.map(v => filterVal.map(j => v[j]));
    },
    //表格选择项改变时触发
    selectionChange(val) {
      val.forEach(item => {
        item.validStatusName=this.$options.filters.filterValid(item.valid)
        item.tradeTypeStatusName=this.$options.filters.filterTradeType(item.tradeType)
        item.paymentCodeStatusName=this.$options.filters.filerPaymentCode(item.paymentCode)
        item.tradeTimeStatusName=this.$options.filters.timestampToTime(item.tradeTime)
      });
      console.log(val);
      this.multipleSelectionData = val;
    },
    //每页条数改变时
    handleSizeChange(page) {
      this.signData.pageSize = page;
      this.getBillWaitListData();
    },
    //当前页改变时
    handleCurrentChange(page) {
      this.signData.pageIndex = page;
      this.getBillWaitListData();
    }
  }
};
