import axios from "axios";
import { getToken } from "@/utils/auth";
import codeSet from "@/components/codeSet";
import treeDept from "@/components/treeDept";
import { filter } from "minimatch";
import { get } from "http";
import PageTable from "@/views/collocation/components/PageTable";
let token = getToken();

export default {
  data() {
    return {
      // 折扣id
      discountId:this.$route.query.discountId,
      // 检索
      filtersData: {
        pageIndex: 1, // 当前页码
        pageSize: 10, // 每页数量
        "orderBy": "",
      },
      // 检索
      filtersData2: {
        pageIndex: 1, // 当前页码
        pageSize: 10, // 每页数量
        "orderBy": "",
      },
      currentMerchantDiscountType:{
        0:'不存在',
        1:'当前折扣值',
        2:'其他折扣值'
      },
      discountType:true,
      dialogTableVisible:false,
      classData: [],
      classList: [],
      cateArr: [],
      cateArrs: [],
      // 季节列表
      codeItem_list_get:[],
      // 全部品牌
      brandOptions:[],
      props: {
        value: "id",
        label: "name",
        children: "children"
      },
      goodsList:{
        data:[]
      },
      // 商户详情
      purchasingAgentPriceManagement_getById_data:{},
      // 获取折扣详情
      purchasingAgentDiscount_get_data:{},
      // 选中某个折扣
      purchasingAgentDiscount_get_data:{},
      // 检索全部商品
      goodsFiltersData:{
        // 当前页码
        pageIndex: 1,
        // 每页数量
        pageSize: 10,
        // 品类id
        cateIdentifier:null,
        // 品牌id
        brandId:null,
        // 售价最小值
        retailPriceLowerLimit:null, 
        // 售价最大值
        retailPriceUpperLimit:null, 
        // 库存最小值
        quantityLowerLimit:null, 
        // 库存最大值
        quantityUpperLimit:null, 
        // 季节
        seasonalStyleCode:null,
        // 关键词
        searchWords:null,
        productDiscountType:1,
        productDiscountValue:null,
        purchaseAgentId:null
      },
      id:this.$route.query.id,
      labelPosition: 'right',
      selectArr:[1, 2, 3],
      goodsData:[{
        name:111
      },{
        name:111
      },{
        name:111
      }],
      formLabelAlign: {
        name: '53424400GM',
        region: '',
        type: ''
      }
    }
  },
  created() {
    const that = this;
    this.codeItem_list(); // 获取季节列表
    this.getBrandOptions(); // 获取品牌
    this.getClassData(); // 获取品类
    this.purchasingAgentPriceManagement_getById(); // 获取商户详情
    this.purchasingAgentDiscount_get(); // 获取折扣详情
  },
  methods: {
     /**
     * 获取品牌
     */
    getBrandOptions() {
      this.request(
        this.api.product.productBrand_list,
        {},
        true
        ).then(data => {
          let _data = data.data;
          this.brandOptions = _data;
      });
    },
    /**
     * 获取分类
     */
    getClassData() {
      //分类类型（1：标准分类，2：定制分类，3：原料分类或者称为属性分类）
      this.$common.getClassSelect(this, 1).then(res => {
        this.classData = res.ztree;
        this.classList = res.list;
      });
    },
    /**
     * 返回折扣
     */
    backList() {
      this.discountType = true;
    },
    /**
     * btn 添加商品
     */
    addProduct() {
      this.dialogTableVisible = true;
      Object.assign(this.filtersData, {
        "purchaseAgentId":this.purchasingAgentDiscount_get_data.purchaseAgentId,
        "productDiscountType":"1",
        "productDiscountValue":this.purchasingAgentDiscount_get_data.productDiscountValue + ''
      });
      if(this.goodsList.data.length > 0) return;
      this.purchasingAgentProduct_scbProductToProductDiscount(); // 添加SCB商品到商品折扣下
    },
    /**
     * 移动到当前折扣
     */
    purchasingAgentDiscount_transferSPU(item) {
      const that = this;
      const text = `将从”${ item.currentMerchantDiscountValue }折”转移到“${ this.purchasingAgentDiscount_get_data.productDiscountValue }折”，是否继续？`;

      this.$confirm(text, '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.request(
          this.api.scb.purchasingAgentDiscount_transferSPU,
          {
            "priceManagementId": this.id,
            "productCode": item.code,
            "productDiscountId": this.purchasingAgentDiscount_get_data.id,
            "oldProductDiscountId":item.currentMerchantDiscountId
          },
          true
        ).then(res => {
          this.warn('移动成功！');
          this.purchasingAgentProduct_scbProductToProductDiscount();// 刷新全部商品
          this.purchasingAgentProduct_productDiscountProductList();// 已添加折扣商品
        });
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '已取消删除'
        });          
      });
    },
    /**
     *btn 剔除
     */
    purchasingAgentDiscount_deleteSPUFromDiscount(code) {
      const that = this;

      this.request(
        this.api.scb.purchasingAgentDiscount_deleteSPUFromDiscount,
        {
          "priceManagementId": this.id,
          "productCode": code,
          "productDiscountId": this.purchasingAgentDiscount_get_data.id
        },
        true
      ).then(res => {
        this.warn('剔除成功！');
        this.purchasingAgentProduct_scbProductToProductDiscount();// 刷新全部商品
        this.purchasingAgentProduct_productDiscountProductList();// 已添加折扣商品
      });
    },
    /**
     * 添加SCB商品到商品折扣下（全部商品）
     */
    purchasingAgentProduct_scbProductToProductDiscount() {
      const that = this;

      this.request(
        this.api.scb.purchasingAgentProduct_scbProductToProductDiscount,
        this.goodsFiltersData,
        true
      ).then(res => {
        that.goodsList = res;
      });
    },
    /**
     * 已添加折扣商品
     */
    purchasingAgentProduct_productDiscountProductList(item) {
      const that = this;
      Object.assign(this.filtersData, {
        "productDiscountId": this.purchasingAgentDiscount_get_data.id
      });
      this.request(
        this.api.scb.purchasingAgentProduct_productDiscountProductList,
        this.filtersData,
        true
      ).then(res => {
        this.goodsData = res;
        res.data.forEach(item => {
          item._cateNames = item.cateNames.join('、');
        });
      });
    },
    /**
     * 添加SPU商品到此折扣下
     */
    purchasingAgentDiscount_addSPUToDiscount(item) {
      const that = this;

      this.request(
        this.api.scb.purchasingAgentDiscount_addSPUToDiscount,
        {
          "priceManagementId": this.id,
          "productCode": item.code,
          "productDiscountId": this.purchasingAgentDiscount_get_data.id
        },
        true
      ).then(res => {
        this.warn('添加成功！');
        this.purchasingAgentProduct_scbProductToProductDiscount();// 刷新全部商品
        this.purchasingAgentProduct_productDiscountProductList();// 已添加折扣商品
      });
    },
    /**
     * 获取商户详情
     */
    purchasingAgentPriceManagement_getById(item) {
      const that = this;

      this.request(
        this.api.scb.purchasingAgentPriceManagement_getById,
        {
          id:this.$route.query.id
        },
        true
      ).then(res => {
        that.purchasingAgentPriceManagement_getById_data = res;
      });
    },
    /**
     * 获取折扣详情
     */
    purchasingAgentDiscount_get(item) {
      const that = this;

      this.request(
        this.api.scb.purchasingAgentDiscount_get,
        {
          id:this.$route.query.discountId
        },
        true
      ).then(res => {
        that.purchasingAgentDiscount_get_data = res;
        this.goodsFiltersData.productDiscountValue = res.productDiscountValue;
        this.goodsFiltersData.purchaseAgentId = res.purchaseAgentId;
        this.purchasingAgentProduct_productDiscountProductList();// 已添加商品
      });
    },
    /**
     * 修改折扣
     * @param { String } value 修改的折扣值
     */
    purchasingAgentDiscount_save(value) {
      const that = this;

      this.request(
        this.api.scb.purchasingAgentDiscount_save,
        {
          id:this.$route.query.discountId,
          priceManagementId:this.$route.query.id,
          "productDiscountType": 1,
          "productDiscountValue": value
        },
        true
      ).then(res => {
        that.purchasingAgentDiscount_get_data = res;
        // 修改完折扣重新刷新列表
        this.purchasingAgentDiscount_get();
      });
    },
    /**
     * 修改某条折扣
     */
    checkDiscount(row) {
      this.discountType = false;
      this.purchasingAgentDiscount_get_data = row;
      this.purchasingAgentProduct_productDiscountProductList();// 已添加折扣商品
    },
    /**
     * （获取季节）
     */
    codeItem_list(item) {
      const that = this;

      this.request(
        this.api.market.codeItem_list,
        {
          codeSetId:'06b11574-ef82-4ae4-a8a9-a25f903083df'
        },
        true
      ).then(res => {
        that.codeItem_list_get = res;
      });
    },
    /**
     * 
     */
    cascaderGet(e) {
      let cateId = JSON.parse(JSON.stringify(e)).pop();
      let obj = this.classList.find(item => item.id == cateId);
      this.goodsFiltersData.cateIdentifier = obj.identifier;
    },
    /**
     * 提示
     * @param { String } message 提示文案
     * @param { String } type 提示类型
     */
    warn(message, type) {
      this.$message({
        message: message,
        type: type || "success"
      });
    },
    open(inputValue) {
      this.$prompt('', '修改采购折扣', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        inputValue:inputValue,
        inputPattern: /(^[1-9](\.\d)?$)|(^[0]\.\d$)/,
        inputErrorMessage: "请输入正确折扣数值！",
        inputPlaceholder:'请输入采购折扣，如5.5折。'
      }).then(({ value }) => {
       this.purchasingAgentDiscount_save(value);
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '取消输入'
        });       
      });
    },
    /**
     * 编辑
     * @param { Object } item 当前对象
     * @param { Boolean } isRead 是否可对写
     * @property { Boolean }isEcho 是否是编辑
     * @return { Object } Obj 返回对象
     */
    timeRedact(item) {
      var Obj = {};
      this.href("discount_order_edit", {
        isEdit: true,
        discountId: item.discountId,
        type: item.type
      });
    },
    /**
     * 获取折扣列表
     */
    marketingMiddleground_discountList() {
      this.request(
        this.api.market.marketingMiddleground_discountList,
        this.filtersData
      ).then(res => {
        this.discountData = res;
      });
    },
    /**
     * 跳转工单详情
     */
    href(routeName, param) {
      this.$router.push({
        path: "/" + routeName,
        name: routeName,
        query: param
      });
    },
    /**
     * 模糊搜索
     */
    handleSearch() {
      this.goodsFiltersData.pageIndex = 1;
      this.purchasingAgentProduct_scbProductToProductDiscount();
    },
    /**
     * 搜索重置
     */
    handleResetFilters() {
      Object.assign(this.goodsFiltersData, {
        // 当前页码
        pageIndex: 1,
        // 每页数量
        pageSize: 10,
        // 品类id
        cateIdentifier:null,
        // 品牌id
        brandId:null,
        // 售价最小值
        retailPriceLowerLimit:null, 
        // 售价最大值
        retailPriceUpperLimit:null, 
        // 库存最小值
        quantityLowerLimit:null, 
        // 库存最大值
        quantityUpperLimit:null, 
        // 季节
        seasonalStyleCode:null,
        // 关键词
        searchWords:null 
      });
      this.cateArr = [];
    },
    /**
     * 点击页数
     * @param { Number } pageIndex 当前页
     */
    handleCurrentChange(pageIndex) {
      this.filtersData.pageIndex = pageIndex;
      this.marketingMiddleground_discountList();
    },
    /**
     *  选择每页多少条数
     *  @param { Number } pageSize 每页条数
     */
    handleSizeChange(pageSize) {
      this.filtersData.pageSize = pageSize;
      this.marketingMiddleground_discountList();
    }
  },
  components: {
    codeSet,
    treeDept,
    PageTable
  }
};
