import PageTable from "@/views/collocation/components/PageTable";
import TimeIntervalCheck from "@/components/TimeIntervalCheck";
import keepAliveMixin from "@/mixins/keepAliveMixin";
export default {
  name: "transfer_slip_list",
  mixins: [keepAliveMixin],
  // 数据
  data() {
    return {
      // 调出方列表
      tuneOut_data: [],
      // 调入方列表
      tuneIn_data: [],
      // 选中调出方信息
      checkTuneOutUser: {},
      // 选中调入方信息
      checkTuneInUser: {},
      orderStarusType: {
        2: "待收货",
        3: "已完成"
      },
      tradeWayCodeType: {
        0: "采购收货",
        1: "调入收货"
      },
      // 参数
      addFormData: {
        // 发货人
        allotMan: "",
        // 发货人直属机构
        organizationId: "",
        organizationName: "",
        // 订单类型
        // orderType: '0203',
        // 发货门店
        outStoreId: "",
        outStoreName: "",
        // 收货门店
        inStoreId: "",
        inStoreName: "",
        // 收货人
        receiveMan: "",
        // 电话
        phone: "",
        // 省市区信息
        provinceName: "",
        provinceCode: "",
        cityName: "",
        cityCode: "",
        areaName: "",
        areaCode: "",
        divisionId: "",
        // 地址
        addressInfo: ""
      },
      // 选择调出弹框
      dialogTableVisible: false,
      // 选择调出类型
      allocatingType: true,
      // 标识是否是零售门店进入
      enterTypeFlag: false,
      // 查询参数
      formData: {
        // 模糊搜索
        searchWords: null,
        // 订单状态
        purchaseOrderStatus: null,
        // 结算类型
        tradeWayCode: null,
        // 门店名称（采购商名称）
        purchaseAgentId: null,
        // 开始时间
        startDate: null,
        // 结束时间
        endDate: null,
        pageIndex: 1,
        pageSize: 10
      },
      // 查询参数
      formDataStore: {
        // 模糊搜索
        searchWords: null,
        // 订单状态
        purchaseOrderStatus: null,
        // 结算类型
        tradeWayCode: null,
        // 门店名称（采购商名称）
        purchaseAgentName: null,
        // 开始时间
        startDate: null,
        // 结束时间
        endDate: null,
        pageIndex: 1,
        pageSize: 10
      },
      // 收货差异
      differenceList: [{ name: "是", id: 1 }, { name: "否", id: 0 }],
      // 发货门店列表
      outStoreList: [],
      // 收货门店列表
      inStoreList: [],
      // 单据状态
      billType: this.$common.getBillType("allot"),
      // 表格数据
      tableData: {},
      // 选中的表格数据
      checkedTableData: [],

      // 备注弹窗
      remarksDialog: false,
      remarksFormData: {
        id: "",
        operationRemarks: ""
      },
      rules: {
        operationRemarks: [
          {
            type: "string",
            required: true,
            message: "请输入内容",
            trigger: "blur"
          }
        ]
      }
    };
  },
  // 组件
  components: {
    PageTable,
    TimeIntervalCheck
  },
  created() {
    this.getTableData();
  },
  // 初始化数据
  mounted() {
  },
  // 方法
  methods: {
    /**
     * 审核通过
     */
    purchaseOrder_checkToPass(row) {
      this.$confirm("审核通过后，库房将接收订单发货通知！", "订单审核", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          this.request(this.api.scb.purchaseOrder_checkToPass, {
            orderId: row.id
          }).then(data => {
            this.getTableData();
            this.warn("审核通过！");
          });
        })
        .catch(() => {
          this.$message({
            type: "info",
            message: "已取消删除"
          });
        });
    },
    /**
     * 提示
     * @param { String } message 提示文案
     * @param { String } type 提示类型
     */
    warn(message, type) {
      this.$message({
        message: message,
        type: type || "success"
      });
    },
    // keepAlive 返回初始化
    activatedInit() {
      this.getTableData();
    },
    // 导出
    exportExcel() {
      if (this.checkedTableData.length === 0) {
        this.$notify.error({ title: "错误", message: "请选择数据" });
        return;
      }
      require.ensure([], () => {
        let { export_json_to_excel } = require("@/vendor/Export2Excel");
        let tHeader = [
          "收货单号",
          "状态",
          "应收数量",
          "sku种类数",

          "实收数量",
          "实收商品货值",
          "收货类型",
          "关联单号",

          "出货方名称",
          "生成时间",
        ];
        // 上面设置Excel的表格第一行的标题
        let filterVal = [
          "receiveNum",
          "receiveStatus",
          "planNum",
          "productQuantity",

          "realityNum",
          "amount",
          "sourceName",
          "allotNum",

          "calloutName",
          "createTime",
        ];
        // 上面的index、nickName、name是tableData里对象的属性
        let list = this.checkedTableData; //把data里的tableData存到list
        let data = this.formatJson(filterVal, list);
        export_json_to_excel(tHeader, data, "收货单");
      });
    },
    formatJson(filterVal, jsonData) {
      return jsonData.map(v => filterVal.map(j => v[j]));
    },
    // 行商品详情导出
    downloadItem(row) {
      this.request(this.api.order.storeAllotOrderProductInfo_list, {
        orderSn: row.orderSn
      }).then(data => {
        this.downloadDataInit(data.data);
        let downloadData = data.data;
        require.ensure([], () => {
          let { export_json_to_excel } = require("@/vendor/Export2Excel");
          let tHeader = [
            "商品款号",
            "商品货号",
            "名称",
            "品牌",
            "吊牌价",
            "调拨单价",
            "调拨数量",
            "调拨金额",
            "门店实发数量",
            "门店实收数量"
          ];
          // 上面设置Excel的表格第一行的标题
          let filterVal = [
            "productCode",
            "goodsCode",
            "goodsName",
            "brandName",
            "_retailPrice",
            "_allotPrice",
            "quantity",
            "_amount",
            "shipmentsQty",
            "receiveQty"
          ];
          // 上面的index、nickName、name是tableData里对象的属性
          let list = downloadData; //把data里的tableData存到list
          let data = this.formatJson(filterVal, list);
          export_json_to_excel(tHeader, data, "调拨商品信息");
        });
      });
    },
    downloadDataInit(data) {
      if (data && data.length) {
        data.forEach(item => {
          item._retailPrice = "￥" + item.retailPrice.toFixed(2);
          item._allotPrice = "￥" + item.allotPrice.toFixed(2);
          item._amount = "￥" + (item.quantity * item.allotPrice).toFixed(2);
        });
      }
    },
    // 点击搜索
    searchTableData() {
      this.formData.pageIndex = 1;
      this.getTableData();
    },
    searchTableDataIn() {
      this.tuneIn_fn();
    },
    initFormDataIn() {},
    // 获取列表数据
    getTableData() {
      this.request(this.api.scb.receiveGoods_list, this.formData).then(
        data => {
          this.tableData = data;
          data.data.forEach((item, idnex) => {
            Object.assign(item, item.purchaseOrderDTO);
            item._purchaseOrderStatus = this.orderStarusType[
              item.purchaseOrderStatus
            ];
            item.amount = item.amount == null ? '--' : item.amount;
            item.receiveStatus = this.statusToName(item.receiveStatus);
            item.createTime = this.$options.filters.timestampToTime(item.createTime);
            item._tradeWayCode = this.tradeWayCodeType[item.tradeWayCode];
          });
        }
      );
    },
    // 获取表格选中的
    tableCheckChange(val) {
      this.checkedTableData = val;
    },
    // 初始查询条件
    initFormData() {
      this.$refs.formData.resetFields();
    },
    // 查看
    seeItem(row) {
      let module = "/" + this.$route.path.split("/")[1];
      this.$router.push({
        path: module + "/order/receiveGoods_order_detail",
        query: {
          id: row.id,
          receiveNum: row.receiveNum
        }
      });
    },
    // 作废
    delItem(row) {
      this.$confirm("确认作废吗？", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          this.request(this.api.scb.purchaseOrder_cancle, {
            orderId: row.id
          }).then(data => {
            this.getTableData();
            this.warn("作废成功！");
          });
        })
        .catch(() => {
          this.$message({
            type: "info",
            message: "已取消删除"
          });
        });
    },
    // 强制完成
    finishItem(row) {
      this.$confirm("确认要强制完成吗？", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          this.request(this.api.scb.purchaseOrder_forceTermination, {
            orderId: row.id
          }).then(data => {
            this.getTableData();
            this.warn("强制成功！");
          });
        })
        .catch(() => {
          this.$message({
            type: "info",
            message: "已取消删除"
          });
        });
    },
    // 备注显示
    remarksItem(row) {
      this.remarksFormData.id = row.id;
      this.remarksFormData.operationRemarks = row.operationRemarks;
      this.remarksDialog = true;
    },
    // 备注提交
    remarksSub() {
      this.$refs.remarksForm.validate(valid => {
        if (valid) {
          this.request(
            this.api.order.storeAllotGoodsOrder_insertRemarks,
            this.remarksFormData
          ).then(data => {
            this.$notify.success({ title: "成功", message: "备注成功" });
            this.remarksDialog = false;
            this.getTableData();
          });
        } else {
          return false;
        }
      });
    },
    // 状态转name
    statusToName(val) {
      if (!val && val !== 0) return;
      let item = this.billType.find(item => {
        return item.id === val;
      });
      if (item) {
        return item.name;
      } else {
        return val;
      }
    },
  },
  filters: {},
  // 计算属性
  computed: {},
  // 监听数据变化
  watch: {}
};
