.item-box {
  padding: 0 30px 30px;
}
.fixed-item-nav {
  position: fixed;
  left: 0;
  top: 0;
  z-index: 1030;
  height: 64px;
  width: 100%;
  padding: 0 18px 0 212px;
  font-size: 20px;
 
  >ul{
     background: #ffffff;
     height: 64px;
  width: 100%;
   color: #333333;
  display: flex;
  padding-left: 20px;
  align-items: center;
  justify-content: flex-start;
  border-bottom: 1px solid #c3c7ce;
  }
  li {
    cursor: pointer;
    font-weight: bold;
    margin-right: 50px;
  }

  li.active {
    color: #2290e8;
  }

  li:hover {
    color: #2290e8;
  }
}

.item-nav {
  height: 64px;
  font-size: 20px;
  color: #333333;
  display: flex;
  align-items: center;
  justify-content: flex-start;
  border-bottom: 1px solid #c3c7ce;
  li {
    cursor: pointer;
    font-weight: bold;
    margin-right: 50px;
  }

  li.active {
    color: #2290e8;
  }

  li:hover {
    color: #2290e8;
  }
}

.basic-wrap {
  margin-top: 25px;
}

.info {
  font-size: 18px;
  color: #000000;
  margin-bottom: 20px;
  border-bottom: 1px solid #eeeeee;
  font-weight: 100;
}
span {
  font-size: 16px;
  color: #999999;
  margin-left: 10px;
  font-weight: 100;
}

.avatar-uploader .el-upload {
  border: 1px dashed #d9d9d9;
  border-radius: 6px;
  cursor: pointer;
  position: relative;
  overflow: hidden;
}

.avatar-uploader .el-upload:hover {
  border-color: #409eff;
}

.c_footer {
  width: 100%;
  height: 80px;
  margin-top: 30px;
}
.img-form {
  /deep/.el-form-item__content {
    display: flex;
    align-items: top;
    justify-content: flex-start;
    .input-w {
      margin-right: 10px;
    }
  }
}
.input-w {
  margin-right: 10px;
}

.img-upload {
  display: flex;
  align-items: top;
  justify-content: flex-start;
  margin-right: 10px;
  /deep/ .el-upload-list--picture {
    margin-left: 10px;
    margin-top: -10px;
    max-width: 200px;
    margin-right: 10px;
  }
}
.el-icon-error {
  padding-top: 8px;
}
.description-wrap {
  .label {
    font-size: 14px;
    color: #000000;
  }
}
// 外部
.stage-wrap {
  display: flex;
  width: 100%;
  margin-top: 20px;
  justify-content: flex-start;
  flex-wrap: wrap;
  > h5 {
    font-size: 14px;
    font-weight: 100;
    width: 80px;
    flex: 0 0 auto;
  }
  .full {
    min-width: 500px;
    flex: 1;
  }
}
.input-h{
  height: 35px;
}
