import PageTable from "@/views/collocation/components/PageTable";
import TimeIntervalCheck from "@/components/TimeIntervalCheck";
import keepAliveMixin from "@/mixins/keepAliveMixin";
import axios from 'axios'
import {getToken} from '@/utils/auth'
import codeSet from '@/components/codeSet'
import treeDept from '@/components/treeDept'
import { filter } from 'minimatch';
import { get } from 'http';
export default {
  name: "transfer_slip_list",
  mixins: [keepAliveMixin],
  // 数据
  data() {
    return {
      fromStore:{
        name:null
      },
      // 调出方列表
      tuneOut_data:[],
      // 调入方列表
      tuneIn_data:[],
      // 选中调出方信息
      checkTuneOutUser:{},
      // 选中调入方信息
      checkTuneInUser:{},
      orderStarusType:{
        100:'待编辑',
        101:'待审核',
        102:'待发货',
        103:'部分已发货',
        104:'异常完成',
        105:'已完成',
        106:'已作废'
      },
      tradeWayCodeType:{
        1:'现金',
        2:'押款代销'
      },
      // 参数
      addFormData: {
        // 发货人
        allotMan: '',
        // 发货人直属机构
        organizationId: '',
        organizationName: '',
        // 订单类型
        // orderType: '0203',
        // 发货门店
        outStoreId: '',
        outStoreName: '',
        // 收货门店
        inStoreId: '',
        inStoreName: '',
        // 收货人
        receiveMan: '',
        // 电话
        phone: '',
        // 省市区信息
        provinceName: '',
        provinceCode: '',
        cityName: '',
        cityCode: '',
        areaName: '',
        areaCode: '',
        divisionId: '',
        // 地址
        addressInfo: ''
      },
      // 选择调出弹框
      dialogTableVisible:false,
      // 选择调出类型
      allocatingType:true,
      // 标识是否是零售门店进入
      enterTypeFlag: false,
      // 查询参数
      formData: {
        // 模糊搜索
        searchWords:null,
        // 订单状态
        purchaseOrderStatus:null,
        // 结算类型
        tradeWayCode:null,
        // 门店名称（采购商名称）
        purchaseAgentId:null,
        // 开始时间
        startDate:null,
        // 结束时间
        endDate:null,
        pageIndex: 1,
        pageSize: 10
      },
      // 查询参数
      formDataStore: {
        // 模糊搜索
        searchWords:null,
        // 订单状态
        purchaseOrderStatus:null,
        // 结算类型
        tradeWayCode:null,
        // 门店名称（采购商名称）
        purchaseAgentName:null,
        // 开始时间
        startDate:null,
        // 结束时间
        endDate:null,
        pageIndex: 1,
        pageSize: 10
      },
      // 收货差异
      differenceList: [{ name: "是", id: 1 }, { name: "否", id: 0 }],
      // 发货门店列表
      outStoreList: [],
      // 收货门店列表
      inStoreList: [],
      // 单据状态
      billType: this.$common.getBillType("transferSlip"),
      // 表格数据
      tableData: {},
      // 选中的表格数据
      checkedTableData: [],

      // 备注弹窗
      remarksDialog: false,
      remarksFormData: {
        id: "",
        operationRemarks: ""
      },
      rules: {
        operationRemarks: [
          {
            type: "string",
            required: true,
            message: "请输入内容",
            trigger: "blur"
          }
        ]
      }
    };
  },
  // 组件
  components: {
    PageTable,
    TimeIntervalCheck
  },
  created() {
    // 删除子页面的门店缓存
    sessionStorage.removeItem("TRANSFER_SLIP_APP_INFO");
    this.info();
  },
  // 初始化数据
  mounted() {
    this.$nextTick(() => {
      this.getInStoreList();
      this.getOutStoreList();
      this.getTableData();
      // 调入、调出门店接口列表，调出传入调入ID（SCB)
      this.tuneIn_fn();
    });
  },
  // 方法
  methods: {
    /**
     * 审核通过
     */
    purchaseOrder_checkToPass(row) {
      this.$confirm('审核通过后，库房将接收订单发货通知！', '订单审核', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.request(
          this.api.scb.purchaseOrder_checkToPass,
          {
            "orderId": row.id
          }
        ).then(data => {
          this.getTableData();
          this.warn('审核通过！');
        });
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '已取消删除'
        });          
      });
      
    },
    async info() {
      // 获取userId
      let info = await this.request(this.api.user.get_userinfo, {}, true);
      // 保存用户名称
      this.addFormData.allotMan = info.name;
      // 获取直属机构
      let organization = await this.request(this.api.user.userCompany_getUserOrgByUserId, {userId: info.userId}, true);
      this.addFormData.organizationId =  organization.id;
      this.addFormData.organizationName =  organization.name;
    },
    /**
     * 获取客户列表
     * @param { String } id 调出方id
     */
    tuneIn_fn() {
      this.request(
        this.api.store.storeBasic_listStoreBasic,
        this.fromStore
      ).then(res => {
        this.tuneIn_data = res;
        res.data.forEach((item, index) => {
          item._createTime = this.$options.filters.timestampToTime(item.createTime);
        });
      });
    },
    /**
     * 调入下一步
     */
    drNext() {
      if(JSON.stringify(this.checkTuneInUser) == '{}') {
          this.warn('请选择渠道客户！', 'warning');
          return; 
        };
      Object.assign(this.addFormData, {
        outStoreId: this.checkTuneInUser.id,
        outStoreName: this.checkTuneInUser.name,
        // 收货门店
        inStoreId: this.checkTuneInUser.id,
        inStoreName: this.checkTuneInUser.name,
        // 收货人
        receiveMan: this.checkTuneInUser.linkman,
        // 电话
        phone: this.checkTuneInUser.phone,
        // 省市区信息
        provinceName: this.checkTuneInUser.provinceName,
        provinceCode: this.checkTuneInUser.provinceCode,
        cityName: this.checkTuneInUser.cityName,
        cityCode: this.checkTuneInUser.cityCode,
        areaName: this.checkTuneInUser.areaName,
        areaCode: this.checkTuneInUser.areaCode,
        divisionId: this.checkTuneInUser.divisionId,
        // 地址
        addressInfo: this.checkTuneInUser.addressInfo
      });
      console.log(this.addFormData);
      sessionStorage.setItem('TRANSFER_SLIP_APP_INFO', JSON.stringify(this.addFormData));
      // return;
      let module = "/" + this.$route.path.split("/")[1];
      this.$router.push({
        path: module + "/order/sell_order_edit"
      });
    },
    /**
     * 提示
     * @param { String } message 提示文案
     * @param { String } type 提示类型
     */
    warn(message, type) {
      this.$message({
        message: message,
        type: type || "success"
      });
    },
    // keepAlive 返回初始化
    activatedInit() {
      this.getTableData();
    },
    // 导出
    exportExcel(row) {
      if (row.purchaseOrderProductDTOS.length === 0) {
        this.$notify.error({ title: "错误", message: "请选择数据" });
        return;
      }
      require.ensure([], () => {
        let { export_json_to_excel } = require("@/vendor/Export2Excel");
        let tHeader = [
          "商品款号",
          "商品货号",
          "商品图片",
          "颜色",
          "规格",
          "品牌",
          "吊牌单价",
          "结算单价",
          "销售数量",
          "实发数量",
        ];
        // 上面设置Excel的表格第一行的标题
        let filterVal = [
          "productCode",
          "goodsCode",
          "image",
          "color",
          "size",
          "brandName",
          "price",
          "purchasePrice",
          "skuModifyQuantity",
          "actualSendQty",
        ];
        // 上面的index、nickName、name是tableData里对象的属性
        let list = row.purchaseOrderProductDTOS; //把data里的tableData存到list
        let data = this.formatJson(filterVal, list);
        export_json_to_excel(tHeader, data, "销售订单商品明细");
      });
    },
    formatJson(filterVal, jsonData) {
      return jsonData.map(v => filterVal.map(j => v[j]));
    },
    // 行商品详情导出
    downloadItem(row) {
      this.request(this.api.order.storeAllotOrderProductInfo_list, {
        orderSn: row.orderSn
      }).then(data => {
        this.downloadDataInit(data.data);
        let downloadData = data.data;
        require.ensure([], () => {
          let { export_json_to_excel } = require("@/vendor/Export2Excel");
          let tHeader = [
            "商品款号",
            "商品货号",
            "名称",
            "品牌",
            "吊牌价",
            "调拨单价",
            "调拨数量",
            "调拨金额",
            "门店实发数量",
            "门店实收数量"
          ];
          // 上面设置Excel的表格第一行的标题
          let filterVal = [
            "productCode",
            "goodsCode",
            "goodsName",
            "brandName",
            "_retailPrice",
            "_allotPrice",
            "quantity",
            "_amount",
            "shipmentsQty",
            "receiveQty"
          ];
          // 上面的index、nickName、name是tableData里对象的属性
          let list = downloadData; //把data里的tableData存到list
          let data = this.formatJson(filterVal, list);
          export_json_to_excel(tHeader, data, "调拨商品信息");
        });
      });
    },
    downloadDataInit(data) {
      if (data && data.length) {
        data.forEach(item => {
          item._retailPrice = "￥" + item.retailPrice;
          item._allotPrice = "￥" + item.allotPrice;
          item._amount = "￥" + (item.quantity * item.allotPrice);
        });
      }
    },
    // 点击搜索
    searchTableData() {
      this.formData.pageIndex = 1;
      this.getTableData();
    },
    searchTableDataIn() {
      this.tuneIn_fn();
    },
    initFormDataIn() {
      Object.assign(this.fromStore, {
        name:null
      });
    },
    // 获取列表数据
    getTableData() {
      this.request(
        this.api.scb.purchaseOrder_list,
        this.formData
      ).then(data => {
        this.initTableData(data.data);
        this.tableData = data;
        data.data.forEach((item, idnex) => {
          Object.assign(item, item.purchaseOrderDTO);
          item._createTime = this.$options.filters.timestampToTime(item.createTime);
          item._purchaseOrderStatus = this.orderStarusType[item.purchaseOrderStatus]; 
          item._tradeWayCode = this.tradeWayCodeType[item.tradeWayCode]; 
        });
      });
    },
    // 初始化数据 导出使用
    initTableData(data) {
      if (data && data.length) {
        data.forEach(item => {
          item._amount = "￥" + item.amount;
          item._difference =
            item.difference === null ? "--" : item.difference ? "是" : "否";
          item._differenceQty =
            item.differenceQty === null ? "--" : item.differenceQty;
          item._orderStatus = this.statusToName(item.orderStatus);
        });
      }
    },
    // 获取表格选中的
    tableCheckChange(val) {
      this.checkedTableData = val;
    },
    // 初始查询条件
    initFormData() {
      Object.assign(this.formData, {
        // 模糊搜索
        searchWords:null,
        // 订单状态
        purchaseOrderStatus:null,
        // 结算类型
        tradeWayCode:null,
        // 门店名称（采购商名称）
        purchaseAgentId:null,
        // 开始时间
        startDate:null,
        // 结束时间
        endDate:null,
        pageIndex: 1,
        pageSize: 10
      })
    },
    // 新增
    addItem() {
      let module = "/" + this.$route.path.split("/")[1];
      this.$router.push({
        path: module + "/order/allotGoods_order_edit"
      });
    },
    // 查看
    seeItem(row) {
      let module = "/" + this.$route.path.split("/")[1];
      this.$router.push({
        path: module + "/order/sell_order_detail",
        query: {
          id: row.id
        }
      });
    },
    // 编辑
    editItem(row) {
      let module = "/" + this.$route.path.split("/")[1];
      this.$router.push({
        path: module + "/order/sell_order_edit",
        query: {
          id: row.id,
          outStoreId:row.purchaseAgentId,
          orderNum:row.purchaseOrderNum
        }
      });
    },
    // 作废
    delItem(row) {
      this.$confirm('确认作废吗？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.request(
          this.api.scb.purchaseOrder_cancle,
          {
            orderId: row.id
          }
        ).then(data => {
          this.getTableData();
          this.warn('作废成功！');
        });
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '已取消删除'
        });          
      });
    },
    // 强制完成
    finishItem(row) {
      this.$confirm('确认要强制完成吗？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.request(
          this.api.scb.purchaseOrder_forceTermination,
          {
            orderId: row.id
          }
        ).then(data => {
          this.getTableData();
          this.warn('强制成功！');
        });
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '已取消删除'
        });          
      });
    },
    // 备注显示
    remarksItem(row) {
      this.remarksFormData.id = row.id;
      this.remarksFormData.operationRemarks = row.operationRemarks;
      this.remarksDialog = true;
    },
    // 备注提交
    remarksSub() {
      this.$refs.remarksForm.validate(valid => {
        if (valid) {
          this.request(
            this.api.order.storeAllotGoodsOrder_insertRemarks,
            this.remarksFormData
          ).then(data => {
            this.$notify.success({ title: "成功", message: "备注成功" });
            this.remarksDialog = false;
            this.getTableData();
          });
        } else {
          return false;
        }
      });
    },
    // 状态转name
    statusToName(val) {
      if (!val && val !== 0) return;
      let item = this.billType.find(item => {
        return item.id === val;
      });
      if (item) {
        return item.name;
      } else {
        return val;
      }
    },
    // 获取发货门店数据
    async getOutStoreList() {
      // 获取userId
      let info = await this.request(this.api.user.get_userinfo, {}, true);
      // 获取直属机构
      let organization = await this.request(
        this.api.user.userCompany_getUserOrgByUserId,
        { userId: info.userId },
        true
      );
      // enableAllocation: 1  (启用门店调拨 后端需要)
      this.request(this.api.retailStore.storeBasic_listBase, {
        organizationId: organization.id,
        enableAllocation: 1
      }).then(data => {
        this.outStoreList = data.data;
      });
    },
    // 获取收货门店数据
    getInStoreList() {
      this.request(this.api.store.storeBasic_listStoreBasic, {}).then(data => {
        this.inStoreList = data.data;
      });
    }
  },
  filters: {},
  // 计算属性
  computed: {},
  // 监听数据变化
  watch: {}
};
